import { Component } from "react";
import Header from "../Header";
import Footer from "../Footer";
import styled from "styled-components";

const HomeContainer = styled.div`
  background-color: #a0a0a0;
  height: 100%;
`;

const IntroSectionContainer = styled.div`
  background-color: #ffdc60;
  height: 86vh;
  display: flex;
  justify-content: center;
  align-items: flex-end;
`;

const IntroSectionLayout = styled.div`
  width: 90%;
  display: flex;
  flex-direction: row;
`;

const IntroImage = styled.img`
  width: 46%;
`;

const IntroTextContainer = styled.div`
  width: 60%;
  height: 380px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const TempleCount = styled.span`
  color: #a4213c;
  font-size: 38px;
  font-weight: bold;
`;

const IntroText = styled.p`
  color: #a4213c;
  font-size: 36px;
  font-weight: 500;
`;

const AddTempleBtn = styled.button`
  background-color: #a4213c;
  color: #ffffff;
  font-size: 18px;
  height: 55px;
  width: 180px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

class Home extends Component {
  render() {
    return (
      <>
        <Header />
        <HomeContainer>
          <IntroSectionContainer>
            <IntroSectionLayout>
              <IntroImage
                src="https://templesofindia.org/assets/images/NewHomeTemple.png"
                alt="temple image"
              />

              <IntroTextContainer>
                <IntroText>
                  <TempleCount>7,50,000</TempleCount> Plus Temples and
                  Counting...
                </IntroText>
                <AddTempleBtn type="button">Add Your Temple</AddTempleBtn>
              </IntroTextContainer>
            </IntroSectionLayout>
          </IntroSectionContainer>
          <h1>Home</h1>
        </HomeContainer>
        <Footer />
      </>
    );
  }
}

export default Home;
