`;

const AddTempleBtn = styled.button``;

class Home extends Component {
  render() {
    return (
      <HomeContainer>
        <IntroSectionContainer>
          <IntroSectionLayout>
            <IntroImageContainer>
              <IntroImage
                src="https://templesofindia.org/assets/images/NewHomeTemple.png"
                alt="temple image"
              />
            </IntroImageContainer>
            <IntroTextContainer>
              <IntroText>
                <TempleCount>7,50,000</TempleCount> Plus Temples and Counting...
              </IntroText>
            </IntroTextContainer>
          </IntroSectionLayout>
        </IntroSectionContainer>
      </HomeContainer>
    );
  }
}

export default Home;
