import { Component } from "react";
import Header from "../Header";
import Footer from "../Footer";
import { Link } from "react-router-dom";
import styled from "styled-components";

const HomeContainer = styled.div`
  background-color: #a0a0a0;
  height: 100%;
`;

const IntroSectionContainer = styled.div`
  background-color: #ffdc60;
  height: 86vh;
  display: flex;
  justify-content: center;
  align-items: flex-end;
`;

const IntroSectionLayout = styled.div`
  width: 90%;
  display: flex;
  flex-direction: row;
`;

const IntroImage = styled.img`
  width: 46%;
`;

const IntroTextContainer = styled.div`
  width: 60%;
  height: 380px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const TempleCount = styled.span`
  color: #a4213c;
  font-size: 38px;
  font-weight: bold;
`;

const IntroText = styled.p`
  color: #a4213c;
  font-size: 36px;
  font-weight: 500;
`;

const AddTempleBtn = styled.button`
  background-color: #a4213c;
  color: #ffffff;
  font-size: 18px;
  height: 55px;
  width: 180px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;
//no

export const HomeDescriptionContent = styled.div`
  display: flex;
  width: 100%;
  background-color: #f2f2f2;
`;
export const FeaturedRecentlyContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 25%;
  height: 80%;
  // border: 1px red solid;
  margin-top: 40px;
`;
export const FeaturedContent = styled.img`
  height: 130px;
  width: 150px;
  margin: 20px;
`;
export const RecentlyEdited = styled.img`
  height: 130px;
  width: 150px;
`;
export const TemplesContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 50%;
  margin-top: 70px;
`;
export const TempleCard = styled.div`
  display: flex;
  flex-direction: column;
  background-color: #ffffff;
  box-shadow: 0.5px 1px 5px 2px #d9d9d9;
  border-radius: 8px;
  margin: 10px;
`;
export const TempleName = styled.h1`
  color: #b32d00;
  font-size: 18px;
`;
export const TemplePlace = styled.p`
  color: #737373;
  font-size: 15px;
`;
export const TempleImage = styled.img``;
export const TempleDescription = styled.p`
  color: #737373;
  font-size: 15px;
`;

export const PostsContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 40px;
  width: 25%;
  margin: 30px;
  max-width: 250px;
`;

export const PostHeading = styled.h1`
