import React, { Component } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";

const NavBar = styled.nav`
  background-color: #ffffff;
  height: 9vh;
  border: 2px solid black;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const WebNameAndLogoContainer = styled.div`
  display: flex;
  align-items: center;
  margin-left: 2%;
`;

const WebLogo = styled.img`
  width: 48px;
  height: 48px;
`;

const WebsiteName = styled.p`
  font-family: "Playfair Display";
  font-size: 32px;
  margin-left: 5px;
`;

const NavItemsContainer = styled.ul`
  padding-left: 0px;
  margin-right: 2%;
  display: flex;
  justify-content: space-between;
  border: 2px solid black;
  width: 680px;
  height: 25px;
`;

const NavItem = styled.li`
  list-style-type: none;
  font-size: 18px;
  font-family: "Mukta";
  font-weight: 500;
`;

const DropdownContainer = styled.ul`
  padding-left: 0px;
  background-color: #f1f1f1;
  width: 100px;
`;

const DropdownItem = styled.li`
  list-style-type: none;
`;

class Header extends Component {
  state = { showDropdown: false };

  toggleDropdown = () => {
    this.setState((prevState) => ({ showDropdown: !prevState.showDropdown }));
  };

  render() {
    const { showDropdown } = this.state;
    return (
      <NavBar>
        <WebNameAndLogoContainer>
          <WebLogo
            src="https://templesofindia.org/assets/images/tofi_logo/tofi-logo_navbar.png"
            alt="tofi logo"
          />
          <WebsiteName>Temples of India</WebsiteName>
        </WebNameAndLogoContainer>
        <NavItemsContainer>
          <Link to="/">
            <NavItem>Home</NavItem>
          </Link>
          <NavItem>Search</NavItem>
          <NavItem>Nearby Temples</NavItem>
          <NavItem>Add Temples</NavItem>
          <Link to="/about">
            <NavItem>About Us</NavItem>
          </Link>
          <NavItem
            onMouseEnter={this.toggleDropdown}
            onMouseLeave={this.toggleDropdown}
          >
            <span>Login</span>
            {showDropdown && (
              <DropdownContainer>
                <Link to="/signup">
                  <DropdownItem>signup</DropdownItem>
                </Link>
                <Link to="/login">
                  <DropdownItem>Login</DropdownItem>
                </Link>
                <Link to="/nearby">
                  <DropdownItem>nearby</DropdownItem>
                </Link>
              </DropdownContainer>
            )}
          </NavItem>
        </NavItemsContainer>
      </NavBar>
    );
  }
}

export default Header;
