import styled from "styled-components";
import { Link } from "react-router-dom";

export const LoginPageContainer = styled.div`
  height: 100vh;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  @media screen and (max-width: 576px) {
    height: 90vh;
    flex-direction: column;
    justify-content: space-between;
  }
  @media screen and (max-width: 992px) {
    height: 100%;
    flex-direction: column;
    justify-content: space-between;
  }
`;

export const LoginImageContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 35%;
`;

export const LoginTempleImage = styled.img`
  height: 130px;
  width: 130px;
`;

export const DownloadText = styled.h1`
  font-size: 60px;
  font-weight: 500;
  color: #039be5;
  text-align: center;
  @media screen and (max-width: 576px) {
    font-size: 25px;
    margin-top: 0px;
  }
  @media screen and (max-width: 992px) {
    font-size: 45px;
    line-height: 80px;
  }
`;

export const LoginDetailsContainer = styled.div`
  height: 460px;
  width: 28%;
  box-shadow: 1px 2px 25px 5px #b3b3b3;
  font-family: "Roboto";
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 30px;
  @media screen and (max-width: 576px) {
    box-shadow: none;
    width: 85%;
    max-width: 350px;
  }
  @media screen and (max-width: 992px) {
    width: 85%;
    max-width: 350px;
  }
`;
export const LoginInContainer = styled.div`
  width: 86%;
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

export const Inputs = styled.input`
  width: 100%;
  box-shadow: 0px 1px 1px 1.2px #d9d9d9;
  height: 40px;
  color: #b3b3b3;
  font-size: 14px;
  font-weight: 400;
  padding-left: 15px;
  border-radius: 2px;
  border: none;
  outline: none;
  margin-bottom: 30px;
`;

export const SignUpButton = styled.button`
  height: 36px;
  width: 80px;
  border-radius: 20px;
  color: #ffffff;
  font-size: 13px;
  font-weight: bold;
  background-color: #e60026;
  box-shadow: 0.5px 2px 4px 3px #b3b3b3;
  text-decoration: none;
  border: none;
  cursor: pointer;
  align-self: center;
`;

export const AlreadyLogin = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-top: 10px;
`;

export const LoginButton = styled.button`
  color: #000000;
  font-size: 16px;
  font-weight: bold;
  background-color: none;
  text-decoration: underline;
  background-color: transparent;
  border: none;
`;

export const OldUser = styled.p`
  color: #000000;
  font-size: 16px;
  font-weight: 500;
`;

const SignUp = () => {
  return (
    <LoginPageContainer>
      <LoginImageContainer>
        <LoginTempleImage
          src="https://res.cloudinary.com/djwd6ayk5/image/upload/v1711387639/tofi-logo_bd4bj9.png"
          alt="temple"
        />
        <DownloadText> Download on Play Store </DownloadText>
      </LoginImageContainer>
      <LoginDetailsContainer>
        <LoginInContainer>
          <Inputs type="text" placeholder="Name" />
          <Inputs type="text" placeholder="Email" />
          <Inputs type="number" placeholder="Phone" />
          <Inputs type="password" placeholder="Password" />
          <SignUpButton to="/signup"> SIGNUP </SignUpButton>
          <AlreadyLogin>
            <OldUser> Already have an account ? </OldUser>
            <LoginButton type="button">Login</LoginButton>
          </AlreadyLogin>
        </LoginInContainer>
      </LoginDetailsContainer>
    </LoginPageContainer>
  );
};

export default SignUp;
