import styled from "styled-components";
import { Link } from "react-router-dom";

export const LoginPageContainer = styled.div`
  height: 100vh;
  width: 100%;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
`;

export const LoginImageContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 30%;
`;

export const LoginTempleImage = styled.img`
  height: 130px;
  width: 130px;
`;

export const DownloadText = styled.h1`
  font-size: 60px;
  font-weight: 500;
  color: #039be5;
  text-align: center;
`;

export const LoginDetailsContainer = styled.div`
  height: 300px;
  width: 28%;
  box-shadow: 1px 2px 25px 5px #b3b3b3;
  font-family: "Roboto";
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 30px;
`;
export const LoginInContainer = styled.div`
  width: 86%;
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

export const Inputs = styled.input`
  width: 100%;
  box-shadow: 0px 1px 1px 1.2px #d9d9d9;
  height: 40px;
  color: #b3b3b3;
  font-size: 14px;
  font-weight: 400;
  padding-left: 15px;
  border-radius: 2px;
  border: none;
  outline: none;
  margin-bottom: 30px;
`;

export const Buttons = styled.div`
  display: flex;
`;

export const SignUpButton = styled(Link)`
  height: 36px;
  width: 72px;
  border-radius: 20px;
  color: #ffffff;
  font-size: 13px;
  font-weight: bold;
  background-color: #e60026;
  box-shadow: 0.5px 2px 4px 3px #b3b3b3;
  text-decoration: none;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

export const AlreadyLogin = styled.div`
  display: flex;
  justify-content: space-between;
`;

export const LoginButton = styled(Link)`
  color: #000000;
  font-size: 13px;
  font-weight: bold;
  background-color: none;
`;

export const ForgotPassword = styled.p`
  color: #000000;
  font-size: 15px;
  font-weight: 400;
  font-family: "Roboto";
`;

const SignUp = () => {
  return (
    <LoginPageContainer>
      <LoginImageContainer>
        <LoginTempleImage
          src="https://res.cloudinary.com/djwd6ayk5/image/upload/v1711387639/tofi-logo_bd4bj9.png"
          alt="temple"
        />
        <DownloadText> Download on Play Store </DownloadText>
      </LoginImageContainer>
      <LoginDetailsContainer>
        <LoginInContainer>
          <Inputs type="text" placeholder="Name" />
          <Inputs type="text" placeholder="Email" />
          <Inputs type="number" placeholder="Phone" />
          <Inputs type="password" placeholder="Password" />
          <Buttons>
            <SignUpButton to="/signup"> SIGNUP </SignUpButton>
          </Buttons>
          <AlreadyLogin>
            <ForgotPassword> Already have an account ? </ForgotPassword>
            <LoginButton to="/login">Login</LoginButton>
          </AlreadyLogin>
        </LoginInContainer>
      </LoginDetailsContainer>
    </LoginPageContainer>
  );
};

export default SignUp;
